import React, { useState } from 'react';
import axios from 'axios';

const DragDropFile = ({ ssetFile, setImageUrl  }) => {
  const [uploading, setUploading] = useState(false);

  const handleDrop = async (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    setFile(droppedFile);
    await handleUpload(droppedFile);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    handleUpload(selectedFile);
  };

  const handleUpload = async (file) => {
    try {
      setUploading(true);
      const formData = new FormData();
      formData.append('file', file);
      const response = await axios.post('/api/uploadPhoto', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setImageUrl(response.data.imageUrl); // Устанавливаем URL загруженной фотографии
    } catch (error) {
      console.error('Error uploading file:', error.message);
    } finally {
      setUploading(false);
    }
  };
  

  return (
    <div>
      <div
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        style={{
          width: '100%',
          height: '200px',
          border: '2px dashed #ccc',
          textAlign: 'center',
          lineHeight: '200px',
        }}
      >
        {uploading ? 'Uploading...' : 'Drop your file here'}
      </div>
      <input type="file" onChange={handleFileChange} />
    </div>
  );
};

export default DragDropFile;
