// Import dependencies
const React = require("react");
const { useState } = require("react");
const Modal = require("./Modal/Modal"); // Use require for importing modules
const { useInput } = require("../hooks/use-form-validate");
const { FontAwesomeIcon } = require("@fortawesome/react-fontawesome");
const { faPersonCircleCheck } = require("@fortawesome/free-solid-svg-icons");
const DragDropFile = require("./DragNDrop/DragDrop.js");
const { uploadPhoto, sendData } = require('G:/GH projects/WTFAdmin/src/components/Api.js'); // Use require for importing modules
const axios = require('axios');

const sign = React.createElement(FontAwesomeIcon, { icon: faPersonCircleCheck, size: "2x", marginRight: "10px" });

function Form() {
  const [modalActive, setModalActive] = useState(false);
  const [file, setFile] = useState(null); // State variable to hold the selected file

  // Inside your component function
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Upload photo
      const photoResponse = await uploadPhoto(file); // Upload the selected file

      // Assuming you have form data available as an object
      const formData = {
        title: title.value,
        link: link.value,
        text: text.value,
        imageUrl: photoResponse.imageUrl // Add the uploaded image URL to the form data
      };

      // Send form data
      const dataResponse = await sendData(formData);

      if (dataResponse) {
        console.log('Data successfully submitted to server');
        setModalActive(true);
      } else {
        console.error('Error submitting data');
      }
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  // Form validation
  const { t } = require("react-i18next");
  const title = useInput("", { isEmpty: true, minLength: 5 });
  const link = useInput("", { isEmpty: true, minLength: 2 });
  const text = useInput("", { isEmpty: true, minLength: 2 });

  return React.createElement('form', { className: 'contact__form', onSubmit: handleSubmit },
  // Rest of the component code
);

}

module.exports = Form; // Export the component
