import React, { useState } from 'react';
import './DragDrop.css';
import { useTranslation } from 'react-i18next';

const DragDropFile = ({ onFileChange }) => {
  const { t } = useTranslation();
  const [uploading, setUploading] = useState(false);
  const [fileName, setFileName] = useState('');

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    onFileChange(droppedFile);
    setFileName(droppedFile.name); // Устанавливаем имя файла
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    onFileChange(selectedFile);
    setFileName(selectedFile.name); // Устанавливаем имя файла
  };

  // const handleButtonClick = () => {
  //   console.log('Button clicked'); // Отладочный вывод
  //   const inputElement = document.getElementById('input-file-upload');
  //   const selectedFile = inputElement.files[0];
  //   onFileChange(selectedFile);
  //   //setFileName(selectedFile.name);
  // };
  

  return (
    <div className="element-animation" id="form-file-upload">
      <input type="file" id="input-file-upload" multiple={true} onChange={handleFileChange} style={{ display: 'none' }} />
      {/* <div
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        id="drag-file-element"
      >
        {uploading ? 'Uploading...' : ' '}
      </div> */}
      <label htmlFor="input-file-upload" id="label-file-upload" className="upload-label" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
  <div >
    <p>{t('post.pic')}</p>
    <button className="upload-button" >
      {t('post.picButton')}
    </button>
  </div>
</label>

      <input type="text" id="file-name" value={fileName} readOnly />
    </div>
  );
};

export default DragDropFile;
