import React from "react";
import './DragDrop.css'
import { useTranslation } from 'react-i18next';
import axios from "axios";
 
// drag drop file component
function DragDropFile() {
  const {t, i18n} = useTranslation()
    // drag state
    const [dragActive, setDragActive] = React.useState(false);
    // ref
    const inputRef = React.useRef(null);
    
    // handle drag events
    const handleDrag = function(e) {
      e.preventDefault();
      e.stopPropagation();
      if (e.type === 'dragenter' || e.type === 'dragover') {
        setDragActive(true);
      } else if (e.type === 'dragleave') {
        setDragActive(false);
      }
    };
    
// triggers when file is dropped
const handleDrop = async function(e) {
  e.preventDefault();
  setDragActive(false);
  const files = e.target.files;
  if (files && files.length > 0) {
    handleFileChange(files);
    // await sendDataToServer(files[0]);
  } else {
    console.error('No files uploaded');
  }
};

const FileUpload = () => {
  const [file, setFile] = useState(null);

const handleUpload = async () => {
  try {
    const formData = new FormData();
    formData.append('file', file);

    const response = await axios.post('/api/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });

    console.log('File uploaded:', response.data);
  } catch (error) {
    console.error('Error uploading file:', error.message);
  }
};

const handleFileChange = (e) => {
  setFile(e.target.files[0]);
};

 
    // function handleFile(files) {
    //   alert("Number of files: " + files.length);
    // }
 
    // triggers when file is selected with click
    const handleChange = async function(e) {
      e.preventDefault();
      const files = e.target.files;
      if (files && files.length > 0) {
        console.log("Selected file:", files[0]);
        handleFile(files);
        // await sendDataToServer(files[0]);
      } else {
        console.error('No files uploaded');
      }
    };
    
    
  // triggers the input when the button is clicked
    const onButtonClick = (e) => {
      e.preventDefault();
      inputRef.current.click();
    };
    
    return (
      
      <form action="/upload" method="POST" encType="multipart/form-data" className="element-animation" id="form-file-upload" onDragEnter={handleDrag} onSubmit={(e) => e.preventDefault()}>
        <input ref={inputRef} type="file" id="input-file-upload" multiple={true} onChange={handleFileChange} />
        <label id="label-file-upload" htmlFor="input-file-upload" for="image" className={dragActive ? "drag-active" : "" }>
          <div>
            <p>{t('post.pic')}</p>
            <button className="upload-button" onClick={handleUpload}>{t('post.picButton')}</button>
          </div> 
        </label>
        { dragActive && <div id="drag-file-element" onDragEnter={(e) => e.preventDefault()} onDragLeave={(e) => e.preventDefault()} onDragOver={(e) => e.preventDefault()} onDrop={handleDrop}></div> }
      </form>
    );
  };
};
 
  export default FileUpload;
