// Import Axios library
const axios = require('axios');

// Function to handle photo upload
const uploadPhoto = async (file) => {
  try {
    const formData = new FormData();
    formData.append('file', file);

    // Send POST request to upload photo
    const response = await axios.post('/api/uploadPhoto', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });

    console.log('Photo uploaded:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error uploading photo:', error.message);
    throw error;
  }
};

// Function to handle form data submission
const sendData = async (data) => {
  try {
    // Send POST request to submit form data
    const response = await axios.post('/api/addData', data);

    console.log('Data submitted:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error submitting data:', error.message);
    throw error;
  }
};

export  { uploadPhoto, sendData }; // Export both functions
