import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import './DragDrop.css'

const DragDropFile = ({ onFileChange }) => {
  const { t } = useTranslation();
  const [uploading, setUploading] = useState(false);
  const [fileName, setFileName] = useState('');
  const [isFileSelected, setIsFileSelected] = useState(false);

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    onFileChange(droppedFile);
    setFileName(droppedFile.name);
    setIsFileSelected(true); // Файл выбран
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    onFileChange(selectedFile);
    setFileName(selectedFile.name);
    setIsFileSelected(true); // Файл выбран
  };

  return (
    <form action="/upload" method="POST" encType="multipart/form-data" className="element-animation" id="form-file-upload" onSubmit={(e) => e.preventDefault()} style={{ height: '16rem', width: '800px', maxWidth: '100%', textAlign: 'center', position: 'relative' }}>
      <div
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        id="drag-file-element"
      >
        {uploading ? 'Uploading...' : ' '}
      </div>
      <input type="file" id="input-file-upload" multiple={true} onChange={handleFileChange} style={{ display: 'none' }} />
      <label id="label-file-upload" htmlFor="input-file-upload" style={{ height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', border: '1px solid var(--aquamarine)', borderRadius: '8px', backgroundColor: 'transparent' }}>
        <div>
          <p>{t('post.pic')}</p>
          <button className="upload-button" disabled={!isFileSelected} style={{ cursor: 'pointer', padding: '15px', fontSize: '1.2rem', border: 'none', color: 'var(--outline)', backgroundColor: 'transparent', marginTop: '10px', borderRadius: '8px', fontFamily: 'Overpass, sans-serif', transition: 'all .15s ease-in-out' }}>
            {t('post.picButton')}
          </button>
          <input type="text" id="file-name" value={fileName} readOnly /> {/* Отображаем имя файла */}
        </div>
        <input type="file" id="input-file-upload" multiple={true} onChange={handleFileChange} style={{ display: 'none' }} />
      </label>
    </form>
  );
};

export default DragDropFile;
