import React, { useEffect, useState  } from 'react';
import axios from 'axios';
import Header from './components/header/Header'
import Page from './components/page/Page.js'
import { Suspense } from 'react'
import { useTranslation } from 'react-i18next';
import useLocalStorage from './hooks/use-localstorage';
import i18n from './i18n';


import './styles/appereance.css'
import './styles/media.css'
import './styles/settings.css'
import './styles/simplebar.css'
import './styles/style.css'


function App() {
  const {t, i18n} = useTranslation()
  const[language, setLanguage] = useLocalStorage('language', 'en')

  const handleLanguageChange = () => {
    const newLanguage = language === 'en' ? 'ru' : 'en'; // Оптимизированный код изменения языка
    i18n.changeLanguage(newLanguage)
    setLanguage(newLanguage)
  }

  // Функция для отправки данных на сервер
  const sendDataToServer = async () => {
    const dataToSend = {
      // Здесь вы можете добавить данные для отправки на сервер
      // Например:
      // name: 'John',
      // email: 'john@example.com'
    };
    try {
      await axios.post('/api/addData', dataToSend);
      console.log('Данные успешно отправлены на сервер');
    } catch (error) {
      console.error('Ошибка при отправке данных на сервер:', error);
    }
  };

  useEffect(() => {
    sendDataToServer();
  }, []); 
  
  return (
    <div>
      <div className="wrapper data-simplebar">
        <Header/>
        <Page/>
      </div>
    </div>
    
  );
}


export default function WrappedApp(){
  return(
    <Suspense fallback='...loading'>
      <App />
    </Suspense>
  )
};
