import React, { useState } from 'react';
import axios from 'axios';

const DragDropFile = () => {
  const [file, setFile] = useState(null);

  const handleDrop = async (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    setFile(droppedFile);
    await handleUpload(droppedFile);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    handleUpload(selectedFile);
  };

  const handleUpload = async (file) => {
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('/api/uploadPhoto', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

       // После успешной загрузки файла получаем путь к файлу на Cloudinary
    const imageUrl = response.data.imageUrl;

    // Вызываем функцию для отправки данных с полученным путем к файлу на сервер
    await sendDataToServer(imageUrl);

      console.log('File uploaded:', response.data);
    } catch (error) {
      console.error('Error uploading file:', error.message);
    }
  };
  const sendDataToServer = async (imageUrl) => {
    try {
      const formData = new FormData();
      formData.append('imageUrl', imageUrl);
  
      // Отправка данных на сервер для записи пути к файлу на Cloudinary
      const response = await axios.post('/api/addData', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
  
      if (response.status === 200) {
        console.log('Данные успешно отправлены на сервер');
        setModalActive(true); // Показываем модальное окно об успешной отправке
      } else {
        console.error('Ошибка при отправке данных на сервер:', response.statusText);
      }
    } catch (error) {
      console.error('Ошибка при отправке данных на сервер:', error.message);
    }
  };

  return (
    <div>
      <div
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        style={{
          width: '100%',
          height: '200px',
          border: '2px dashed #ccc',
          textAlign: 'center',
          lineHeight: '200px',
        }}
      >
        Drop your file here
      </div>
      <input type="file" onChange={handleFileChange} />
    </div>
  );
};

export default DragDropFile;
