import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import Modal from "./Modal/Modal";
import { useInput } from "../hooks/use-form-validate";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPersonCircleCheck } from "@fortawesome/free-solid-svg-icons";
import DragDropFile from "./DragNDrop/DragDrop.js";
import axios from "axios";
import { uploadPhoto, sendData } from 'G:/GH projects/WTFAdmin/src/components/Api.js'; 

const sign = <FontAwesomeIcon icon={faPersonCircleCheck} size="2x" marginRight="10px" />;

function Form() {
  const [modalActive, setModalActive] = useState(false);
  const [file, setFile] = useState(null); // Define file and setFile

  // Inside your component function
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = {
        title: title.value,
        link: link.value,
        text: text.value,
        file: file 
      };

      const dataResponse = await sendData(formData);

      if (dataResponse) {
        console.log('Data successfully submitted to server');
        setModalActive(true);
      } else {
        console.error('Error submitting data');
      }
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  // Form validation
  const { t } = useTranslation();
  const title = useInput("", { isEmpty: true, minLength: 5 });
  const link = useInput("", { isEmpty: true, minLength: 2 });
  const text = useInput("", { isEmpty: true, minLength: 2 });

  return (
    <form className="contact__form" onSubmit={handleSubmit}>
      {/* Your form inputs */}
      <DragDropFile setFile={setFile} /> {/* Pass setFile function as prop */}

      {/* Your form submit button */}
      <button
        disabled={!title.inputValid || !link.inputValid || !text.inputValid}
        className="form__btn form__control element-animation"
        type="submit"
        name="submit"
      >
        {t("post.button")}
      </button>
      
      {/* Your modal */}
      <Modal active={modalActive} setActive={setModalActive}>
        {sign}
        <p>{t("contact.modalThanks")}</p>
        <br />
        <p>{t("contact.modalRequest")}</p>
      </Modal>
    </form>
  );
}

export default Form;
