import React, { useState } from 'react';
import { uploadPhoto } from 'G:/GH projects/WTFAdmin/src/components/Api.js'; // Import the Axios function for uploading photos

const DragDropFile = () => {
  const [file, setFile] = useState(null);

  const handleDrop = async (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    setFile(droppedFile);
    await handleUpload(droppedFile);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    handleUpload(selectedFile);
  };

  const handleUpload = async (file) => {
    try {
      const imageUrl = await uploadPhoto(file); // Upload the photo using Axios

      console.log('File uploaded:', imageUrl);
    } catch (error) {
      console.error('Error uploading file:', error.message);
    }
  };
  
  return (
    <div>
      <div
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        style={{
          width: '100%',
          height: '200px',
          border: '2px dashed #ccc',
          textAlign: 'center',
          lineHeight: '200px',
        }}
      >
        Drop your file here
      </div>
      <input type="file" onChange={handleFileChange} />
    </div>
  );
};

export default DragDropFile;
