import React, { useState } from 'react';
import './DragDrop.css'
import { useTranslation } from 'react-i18next';

const DragDropFile = ({ onFileChange }) => {
  const { t } = useTranslation();
  const [uploading, setUploading] = useState(false);
  const [fileName, setFileName] = useState('');
  const inputRef = React.useRef(null);

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    onFileChange(droppedFile);
    setFileName(droppedFile.name); // Устанавливаем имя файла
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    onFileChange(selectedFile);
    setFileName(selectedFile.name); // Устанавливаем имя файла
  };

  const handleButtonClick = (e) => {
    e.preventDefault();
    inputRef.current.click();
  };

  return (
    <form action="/upload" method="POST" encType="multipart/form-data" className="element-animation" id="form-file-upload" onSubmit={(e) => e.preventDefault()}>
      <div
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        id="drag-file-element"
      >
        {uploading ? 'Uploading...' : ' '}
      </div>
      <input ref={inputRef} type="file" id="input-file-upload" multiple={true} onChange={handleFileChange} />
      <label id="label-file-upload" htmlFor="input-file-upload" style={{ height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', border: '1px solid var(--aquamarine)', borderRadius: '8px', backgroundColor: 'transparent' }}>
        <div>
          <p>{t('post.pic')}</p>
          <button className="upload-button" onClick={handleButtonClick}>
            {t('post.picButton')}
          </button>
          <input type="text" id="file-name" value={fileName} readOnly /> {/* Отображаем имя файла */}
          {/* <input type="file" id="input-file-upload" multiple={true} onChange={handleFileChange} /> */}

        </div>
      </label>
    </form>
  );
};

export default DragDropFile;
